const int N = 3;
const int INF = 15000001;

class S{
public:
  int t[N], cost;
  S(int a = 0, int b = 0, int c = 0, int cost = 0) : cost(cost){
    t[0] = a;
    t[1] = b;
    t[2] = c;
  }
};

int n, m;
//int ans;

int calc(S s, int pres, int pret){
  //if(ans <= s.cost) return INF;
  if(s.t[0] == (1 << n) - 1 || s.t[2] == (1 << n) - 1) return s.cost;
  if(s.cost >= m) return INF;
  int res = INF;
  int tmp[N];
  fill(tmp, tmp+N, 0);
  for(int j=0;j<N;j++){
    for(int i=n-1;i>=0;i--){
      if(s.t[j] & 1 << i){
        tmp[j] = i+1;
        break;
      }
    }
  }
  for(int i=0;i<N;i++){
    if(tmp[i]){
      for(int j=0;j<N;j++){
        if(abs(i-j) != 1) continue;
        if(i == pret && j == pres) continue;
        if(tmp[i] > tmp[j]){
          S t = s;
          t.t[i] &= (~(1 << (tmp[i]-1)));
          t.t[j] |= (1 << (tmp[i]-1));
          t.cost++;
          res = min(res, calc(t, i, j));
          //ans = min(ans, res);
        }
      }
    }
  }
  return res;
}

int solve(S s){
  if(s.t[0] == (1 << n) - 1 || s.t[2] == (1 << n) - 1) return s.cost;
  int res = INF;
  int tmp[N];
  fill(tmp, tmp+N, -1);
  for(int j=0;j<N;j++){
    for(int i=n-1;i>=0;i--){
      if(s.t[j] & 1 << i){
        tmp[j] = i+1;
        break;
      }
    }
  }
  for(int i=0;i<N;i++){
    if(tmp[i]){
      for(int j=0;j<N;j++){
        if(abs(i-j) != 1) continue;
        if(tmp[i] > tmp[j]){
          S t = s;
          t.t[i] &= (~(1 << (tmp[i]-1)));
          t.t[j] |= (1 << (tmp[i]-1));
          t.cost++;
          res = min(res, calc(t, i, j));
          //ans = min(ans, res);
        }
      }
    }
  }
  if(res == INF) return -1;
  return res;
}

