int N, M;

int solve(array<int, 3> const& target) {
  queue<array<int, 3>> q;
  int num = 0;
  rep(i, N) num |= 1 << i;
  q.push(array<int, 3>{{num, 0, 0}});
  q.push(array<int, 3>{{0, 0, num}});

  map<array<int, 3>, int> mp;
  mp[array<int, 3>{{num, 0, 0}}] = 0;
  mp[array<int, 3>{{0, 0, num}}] = 0;

  while(!q.empty()) {
    auto v = q.front(); q.pop();
    if(v == target) {
      return mp[v];
    }

    rep(i, v.size()) {
      for(int k=N-1; k>=0; k--) {
        if(v[i] >> k & 1) {
          if(i + 1 < 3) {
            auto nv = v;
            nv[i] ^= 1 << k;
            if(nv[i + 1] < (1<<k)) {
              nv[i + 1] ^= 1 << k;
              if(mp.find(nv) == mp.end() && mp[v] + 1 <= M) {
                mp[nv] = mp[v] + 1;
                q.push(nv);
              }
            }
          }

          if(i - 1 >= 0) {
            auto nv = v;
            nv[i] ^= 1 << k;
            if(nv[i - 1] < (1<<k)) {
              nv[i - 1] ^= 1 << k;
              if(mp.find(nv) == mp.end() && mp[v] + 1 <= M) {
                mp[nv] = mp[v] + 1;
                q.push(nv);
              }
            }
          }

          break;
        }
      }
    }
  }

  return -1;
}

