using namespace std;

using State=vector<stack<int>>;
struct Scene{
	State state;
	int prev_from;
	int prev_to;
	int count;
	Scene(State state, int prev_from, int prev_to, int count) : 
		state(state), prev_from(prev_from), prev_to(prev_to), count(count){}
};

void enqueue(queue<Scene>& scene, Scene& s, int from, int to){
	if(s.prev_from==to && s.prev_to==from) return;
	if(s.state[from].empty()) return;
	if(!s.state[to].empty() && s.state[from].top()<s.state[to].top()) return;
	State t;
	copy(s.state.begin(), s.state.end(), back_inserter(t));
	t[to].push(t[from].top()); t[from].pop();
	scene.push(Scene(t, from, to, s.count+1));
}

void dump(Scene& s){
	cout << "--------------------------------------------------\n";
	cout << s.prev_from << "|" << s.prev_to << "|" << s.count << "\n";
	State t;
	copy(s.state.begin(), s.state.end(), back_inserter(t));
	while(!t[0].empty()){ cout << t[0].top() << ", "; t[0].pop(); } cout << "\n";
	while(!t[1].empty()){ cout << t[1].top() << ", "; t[1].pop(); } cout << "\n";
	while(!t[2].empty()){ cout << t[2].top() << ", "; t[2].pop(); } cout << "\n";
	cout << "--------------------------------------------------\n";
}

int solve(State init, int m){
	queue<Scene> scene;
	scene.push(Scene(init, -1, -1, 0));
	while(!scene.empty()){
		auto s=scene.front();
		//dump(s);
		if(s.state[1].empty() && (s.state[0].empty() || s.state[2].empty())){
			return s.count;
		}
		if(s.count>m) return -1;
		enqueue(scene, s, 0, 1);
		enqueue(scene, s, 1, 0);
		enqueue(scene, s, 1, 2);
		enqueue(scene, s, 2, 1);
		scene.pop();
	}
	return -1;
}

int main(){
	while(true){
		int n, m; cin >> n >> m;
		if(n==0 && m==0) break;
		State s;
		for(int i=0; i<3; ++i){
			s.push_back(stack<int>());
			int x; cin >> x;
			for(int j=0; j<x; ++j){
				int y; cin >> y;
				s[i].push(y);
			}
		}
		cout << solve(s, m) << "\n";
	}
	return 0;
}

