		}

		vi a(N+1), c(N+1), p(N+1);
		p[N] = 2;			// required steps to move A->C or C->A
		a[N] = v[N];
		c[N] = 2 - v[N];
		for(int n=N; n>1; n--) {
			p[n-1] = 3 * p[n] + 2;
			switch(v[n-1]) {
			case 0:
				a[n-1] = a[n];
				c[n-1] = c[n] + 2 * p[n] + 2;
				break;
			case 1:
				a[n-1] = c[n] + p[n] + 1;
				c[n-1] = a[n] + p[n] + 1;
				break;
			case 2:
				a[n-1] = a[n] + 2 * p[n] + 2;
				c[n-1] = c[n];
				break;
			}
		}
		int res = min(a[1], c[1]);
		if(M < res) res = -1;
		cout << res << endl;
	}
}

