int count(stack<int> s[3], int m, int n, int index){

  int count = 0;
  int flag = 1;

  while(1){

    if((s[0].empty() && s[1].empty()) || (s[1].empty() && s[2].empty())) break;

    if(!flag || s[1].empty()){ 
      if(index == 0){
	s[2].push(s[index].top());
	s[index].pop();
	count+=2;
	index = 2; 
      }     
      else if (index == 2){
	s[0].push(s[index].top());
	s[index].pop();
	count+=2;
	index = 0;      
      }
    }

    if((s[0].empty() && s[1].empty()) || (s[1].empty() && s[2].empty())) break; 

    if(s[0].empty()){
      s[0].push(s[1].top());
	s[1].pop();
	count++;
    }     
    else if(s[2].empty()){
      s[2].push(s[1].top());
      s[1].pop();
      count++;
    }  
    else if(s[1].empty()){
      s[1].push(s[(index+2)%4].top());
      s[(index+2)%4].pop();
      count++;
    }
    else{
      if(s[0].top() > s[2].top()){
	s[2].push(s[1].top());
	s[1].pop();
	count++;
      }
      else if(s[0].top() < s[2].top()){
	s[0].push(s[1].top());
	s[1].pop();
	count++;
      }
    }
    if(m+1 < count) return -1;
    flag = 0;
  }
  return count;
}


