typedef long long ll;
typedef pair <int,vector<stack<int> > > P;
typedef pair <int,P> PP;
typedef pair <int,PP> PPP;

static const double eps = 1e-8;

int bfs(int m,vector<stack<int> >& stks, map<vector<stack<int> >,int>& visited){

	priority_queue<P,vector<P>,greater<P> > que;
	que.push(P(0,stks));
	visited[stks] = 0;

	while(!que.empty()){
		int cost = que.top().first;


		vector<stack<int> > stks = que.top().second;

		if(stks[1].empty() && stks[2].empty()) return cost;
		if(stks[0].empty() && stks[1].empty()) return cost;

		if(cost >= m) break;
		que.pop();
		for(int i=0;i+1<stks.size();i++){
			if((!stks[i].empty() && !stks[i+1].empty() && stks[i].top() < stks[i+1].top()) 
				|| (stks[i].empty() && !stks[i+1].empty())){
				vector<stack<int> > tmp = stks;
				tmp[i].push(tmp[i+1].top());
				tmp[i+1].pop();
				if(visited.find(tmp) == visited.end()){
					visited[tmp] = cost + 1;
					que.push(P(cost+1,tmp));
				}
			}

			else if((!stks[i].empty() && !stks[i+1].empty() && stks[i].top() > stks[i+1].top()) 
				|| (!stks[i].empty() && stks[i+1].empty())){
				vector<stack<int> > tmp = stks;
				tmp[i+1].push(tmp[i].top());
				tmp[i].pop();
				if(visited.find(tmp) == visited.end()){
					visited[tmp] = cost + 1;
					que.push(P(cost+1,tmp));
				}
			}
		}
	}
	return -1;
}

