using namespace std;

int bg(int cup)
{
    int ans = 0;
    loop (20, i) if (cup & 1<<i) ans = i;
    return ans;
}

int calc(vector<int> cup, int m, int prev)
{
    if (m < 0) return -1;
    if (!cup[0] && !cup[1] || !cup[1] && !cup[2]) return m;

    int i, j;
    if (!prev) i = 1, j = 2;
    else i = 0, j = 1;

    if (bg(cup[i]) && bg(cup[i]) > bg(cup[j])) {
        cup[j] ^= 1<<bg(cup[i]);
        cup[i] ^= 1<<bg(cup[i]);
        return calc(cup, m-1, !prev);
    } else if (bg(cup[j]) && bg(cup[j]) > bg(cup[i])){
        cup[i] ^= 1<<bg(cup[j]);
        cup[j] ^= 1<<bg(cup[j]);
        return calc(cup, m-1, !prev);
    }
    return -1;
}

int main()
{
    while (1) {
        int n, m; cin >> n >> m;
        if (n == 0 && m == 0) break;
        vector<int> cup(3);
        loop (3, i) {
            int c; cin >> c;
            while (c--) {
                int v; cin >> v;
                cup[i] |= 1<<v;
            }
        }
        int ans = max({
                calc(cup, m, 0),
                calc(cup, m, 1)});
        cout << (ans < 0 ? -1 : m-ans) << endl;
    }
    return 0;
}

