void move(stack<int> A, stack<int> B, stack<int> C, int count, int exp){
	int temp;
	if(count>maxcount)return;
	if((A.empty()||C.empty())&&B.empty()){
		if(count<mincount)mincount=count;
	}
	if(exp!=1&&((B.empty()&&A.empty()==false)||(B.empty()==false&&A.empty()==false&&A.top()>B.top()))){
		//0
		temp=A.top();
		A.pop();
		B.push(temp);
		count++;
		move(A,B,C,count,0);
		count--;
		B.pop();
		A.push(temp);
	}
	if(exp!=0&&((A.empty()&&B.empty()==false)||(A.empty()==false&&B.empty()==false&&B.top()>A.top()))){
		//1
		temp=B.top();
		B.pop();
		A.push(temp);
		count++;
		move(A,B,C,count,1);
		count--;
		A.pop();
		B.push(temp);
	}
	if(exp!=3&&((B.empty()&&C.empty()==false)||(B.empty()==false&&C.empty()==false&&C.top()>B.top()))){
		//2
		temp=C.top();
		C.pop();
		B.push(temp);
		count++;
		move(A,B,C,count,2);
		count--;
		B.pop();
		C.push(temp);
	}
	if(exp!=2&&((C.empty()&&B.empty()==false)||(C.empty()==false&&B.empty()==false&&B.top()>C.top()))){
		//3
		temp=B.top();
		B.pop();
		C.push(temp);
		count++;
		move(A,B,C,count,3);
		count--;
		C.pop();
		B.push(temp);
	}
}

#include <cstdio>

int an, bn, cn, a[20], b[20], c[20], total = 0, f = 1;

struct mm{
	char ch;
	int nu;
};

struct mm m1, m2;

int judge(int max, int temp){
	if(temp + 1 == an + bn + cn)return -1;
	if(a[an] == max - temp)return 50 + temp;
	if(c[cn] == max - temp)return 100 + temp;
	if(b[cn - temp] == max - temp)return judge(max, temp + 1);
	return 0;
}

int calculation(int min, int max){
	int sum;
	if(min == max)return 0;
	if(min < max)sum = calculation(++min, max) * 3 + 2;
	return sum;
}

int move(int flag){
	int number;
	if(flag == 2){  //right
		number = an - m1.nu + 1;
		for(int i = 1;i <= number;i++)c[++cn] = a[an--];
	}
	if(flag == 1){
		number = cn - m1.nu + 1;
		for(int i = 1;i <= number;i++)a[++an] = c[cn--];
	}
	//printf("*%d %d %d*", an, m1.nu, number);
	total += calculation(0, number);
	//printf("*%d*", number);
	return 0;
}

int sort(int max){
	if(m1.ch == m2.ch)return 0;
	if(m2.ch == 'b'){
		if(m1.ch == 'a')c[++cn] = b[bn--], move(2);  //right
		if(m1.ch == 'c')a[++an] = b[bn--], move(1);
		total++;
	}else if(m2.ch == 'a'){
		if(c[cn - 1] == max - 2){
			int te = an;
			b[++bn] = a[an--], move(1);
			m1.ch = 'a', m1.nu = te;
			c[++cn] = b[bn--], move(2);
		}else{
			move(1);
		}
	}else if(m2.ch == 'c'){
		if(a[an - 1] == max - 2){
			int te = cn;
			b[++bn] = c[cn--], move(2);
			m1.ch = 'c', m1.nu = te;
			a[++an] = b[bn--], move(1);
		}else{
			move(2);
		}
	}
	return 0;
}

int solve(int max){
	for(int i = 1;i <= an;i++){
		if(a[i] == max)m1.ch = 'a',m1.nu = i;
		if(a[i] == max - 1)m2.ch = 'a', m2.nu = i;
	}
	for(int i = 1;i <= bn;i++){
		if(b[i] == max)m1.ch = 'b',m1.nu = i;
		if(b[i] == max - 1)m2.ch = 'b', m2.nu = i;
	}
	for(int i = 1;i <= cn;i++){
		if(c[i] == max)m1.ch = 'c',m1.nu = i;
		if(c[i] == max - 1)m2.ch = 'c', m2.nu = i;
	}
	if(f == 1 && m1.ch == 'b'){
		int vec = 0;  //1 = left 
		if(m2.ch == 'b'){
			int temp = judge(max, 2);
			if(temp == -1)return 0;
			if(temp >= 100){
				if(temp % 2)vec = 2;
				else vec = 1;
			}else if(temp >= 50){
				if(temp % 2)vec = 1;
				else vec = 2;
			}
		}
		if(m2.ch == 'a'){
			if(vec == 1)a[++an] = b[bn--];
			if(c[cn] == max - 2){
				c[++cn] = a[an--], c[++cn] = b[bn++];
				total += 7;
				return 0;
			}
		}
		if(m2.ch == 'c'){
			if(vec == 2)c[++cn] = b[bn--];
			if(a[an] == max - 2){
				a[++an] = c[cn--], a[++an] = b[bn++];
				total += 7;
				return 0;
			}
		}
		//printf("*%d*", total);
		total++;
	}
	sort(max);
	//printf("*%d*", total);

	return 0;
}

int main(){
	int n, m, max = 0;
	while(scanf("%d %d",&n, &m)){
		if(n == 0 && m == 0)break;
		max = total = 0, f = 1;


		scanf("%d", &an);
		for(int i = 1;i <= an;i++){
			scanf("%d", &a[i]);
			if(a[i] > max)max = a[i];
		}
		scanf("%d", &bn);
		for(int i = 1;i <= bn;i++){
			scanf("%d", &b[i]);
			if(b[i] > max)max = b[i];
		}
		scanf("%d", &cn);
		for(int i = 1;i <= cn;i++){
			scanf("%d", &c[i]);
			if(c[i] > max)max = c[i];
		}

		for(int i = 0;i < n - 1;i++)solve(max - i);

		printf("%d\n", total);
		//if(total <= m)printf("%d\n", total);
		//else printf("-1\n");
	}
	return 0;
}

