const char Max_n = 15;

int f_A(const std::deque<char> &stack_A, const std::deque<char> &stack_B, const std::deque<char> &stack_C, int idx_A, int idx_B, int idx_C);
int f_B(const std::deque<char> &stack_A, const std::deque<char> &stack_B, const std::deque<char> &stack_C, int idx_A, int idx_B, int idx_C);
int f_C(const std::deque<char> &stack_A, const std::deque<char> &stack_B, const std::deque<char> &stack_C, int idx_A, int idx_B, int idx_C);

int f_A(const std::deque<char> &stack_A, const std::deque<char> &stack_B, const std::deque<char> &stack_C, int idx_A, int idx_B, int idx_C){
	if(idx_B >= stack_B.size() && idx_C >= stack_C.size()){
		return 0;
	}
	char min_stack_A = !(idx_A >= stack_A.size()) ? stack_A[idx_A] : Max_n + 1;
	char min_stack_B = !(idx_B >= stack_B.size()) ? stack_B[idx_B] : Max_n + 1;
	char min_stack_C = !(idx_C >= stack_C.size()) ? stack_C[idx_C] : Max_n + 1;

	if(min_stack_A < min_stack_B && min_stack_A < min_stack_C){
		idx_A++;
		return f_A(stack_A, stack_B, stack_C, idx_A, idx_B, idx_C);
	}
	if(min_stack_B < min_stack_A && min_stack_B < min_stack_C){
		int n = 0;

		idx_B++;
		n = stack_A.size() + stack_B.size() + stack_C.size() - (idx_A + idx_B + idx_C);
		return f_C(stack_A, stack_B, stack_C, idx_A, idx_B, idx_C) + int(pow(3, n));
	}
	if(min_stack_C < min_stack_A && min_stack_C < min_stack_B){
		int n = 0;

		idx_C++;
		n = stack_A.size() + stack_B.size() + stack_C.size() - (idx_A + idx_B + idx_C);
		return f_A(stack_A, stack_B, stack_C, idx_A, idx_B, idx_C) + 2 * int(pow(3, n));
	}

	//std::cout << "error f_A" << std::endl;
	//exit(0);
	return 0;
}

int f_B(const std::deque<char> &stack_A, const std::deque<char> &stack_B, const std::deque<char> &stack_C, int idx_A, int idx_B, int idx_C){
	if(idx_A >= stack_A.size() && idx_C >= stack_C.size()){
		return 0;
	}
	char min_stack_A = !(idx_A >= stack_A.size()) ? stack_A[idx_A] : Max_n + 1;
	char min_stack_B = !(idx_B >= stack_B.size()) ? stack_B[idx_B] : Max_n + 1;
	char min_stack_C = !(idx_C >= stack_C.size()) ? stack_C[idx_C] : Max_n + 1;

	if(min_stack_A < min_stack_B && min_stack_A < min_stack_C){
		int n = 0;

		idx_A++;
		n = stack_A.size() + stack_B.size() + stack_C.size() - (idx_A + idx_B + idx_C);
		return f_C(stack_A, stack_B, stack_C, idx_A, idx_B, idx_C) + (int(pow(3, n)) + 1) / 2;
	}
	if(min_stack_B < min_stack_A && min_stack_B < min_stack_C){
		idx_B++;
		return f_B(stack_A, stack_B, stack_C, idx_A, idx_B, idx_C);
	}
	if(min_stack_C < min_stack_A && min_stack_C < min_stack_B){
		int n = 0;

		idx_C++;
		n = stack_A.size() + stack_B.size() + stack_C.size() - (idx_A + idx_B + idx_C);
		return f_A(stack_A, stack_B, stack_C, idx_A, idx_B, idx_C) + (int(pow(3, n)) + 1) / 2;
	}

	//std::cout << "error f_B" << std::endl;
	//exit(0);
	return 0;
}

int f_C(const std::deque<char> &stack_A, const std::deque<char> &stack_B, const std::deque<char> &stack_C, int idx_A, int idx_B, int idx_C){
	if(idx_A >= stack_A.size() && idx_B >= stack_B.size()){
		return 0;
	}
	char min_stack_A = !(idx_A >= stack_A.size()) ? stack_A[idx_A] : Max_n + 1;
	char min_stack_B = !(idx_B >= stack_B.size()) ? stack_B[idx_B] : Max_n + 1;
	char min_stack_C = !(idx_C >= stack_C.size()) ? stack_C[idx_C] : Max_n + 1;

	if(min_stack_A < min_stack_B && min_stack_A < min_stack_C){
		int n = 0;

		idx_A++;
		n = stack_A.size() + stack_B.size() + stack_C.size() - (idx_A + idx_B + idx_C);
		return f_C(stack_A, stack_B, stack_C, idx_A, idx_B, idx_C) + 2 * int(pow(3, n));
	}
	if(min_stack_B < min_stack_A && min_stack_B < min_stack_C){
		int n = 0;

		idx_B++;
		n = stack_A.size() + stack_B.size() + stack_C.size() - (idx_A + idx_B + idx_C);
		return f_A(stack_A, stack_B, stack_C, idx_A, idx_B, idx_C) + int(pow(3, n));
	}
	if(min_stack_C < min_stack_A && min_stack_C < min_stack_B){
		idx_C++;
		return f_C(stack_A, stack_B, stack_C, idx_A, idx_B, idx_C);
	}

	//std::cout << "error f_C" << std::endl;
	//exit(0);
	return 0;
}

