void bfs(stack<int> si[3],int f,int t){
	state hoge;
	for(int i=0 ;i<3; ++i) hoge.s[i]=si[i];
	hoge.count=0;
	hoge.from=f;
	hoge.to=t;
	queue<state> que;
	que.push(hoge);
	while(!que.empty()){
		state st=que.front();
		que.pop();
		//cout << st.count << endl;
		//if(st.count>m) continue;
		if(st.s[0].size()==n||st.s[2].size()==n){
			ans=min(ans,st.count);
			return;
		}
		if(!(st.from==1&&st.to==0)&&st.s[0].size()!=0&&(st.s[1].size()==0||st.s[0].top()>st.s[1].top())){
			state ns=st;
			ns.s[1].push(ns.s[0].top());
			ns.s[0].pop();
			++ns.count;
			ns.from=0;
			ns.to=1;
			que.push(ns);
		}
		if(!(st.from==2&&st.to==1)&&st.s[1].size()!=0&&(st.s[2].size()==0||st.s[1].top()>st.s[2].top())){
			state ns=st;
			ns.s[2].push(ns.s[1].top());
			ns.s[1].pop();//
			++ns.count;
			ns.from=1;
			ns.to=2;
			que.push(ns);
		} else if(!(st.from==0&&st.to==1)&&st.s[1].size()!=0&&(st.s[0].size()==0||st.s[1].top()>st.s[0].top())){
			state ns=st;
			ns.s[0].push(ns.s[1].top());
			ns.s[1].pop();
			++ns.count;
			ns.from=1;
			ns.to=0;
			que.push(ns);
		}
		if(!(st.from==1&&st.to==2)&&st.s[2].size()!=0&&(st.s[1].size()==0||st.s[2].top()>st.s[1].top())){
			state ns=st;
			ns.s[1].push(ns.s[2].top());
			ns.s[2].pop();
			++ns.count;
			ns.from=2;
			ns.to=1;
			que.push(ns);
		}
	}
}

