const Int MOD = 1000000007;

Int mod_pow(Int x, Int n) {
    Int res = 1;
    while(n > 0) {
        if(n & 1) res = (res * x) % MOD; //(1)
        x = (x * x) % MOD;
        n >>= 1; //(n = n >> 1)
    }
    return res;
}

int Dist[16];
int N, M;
int crt;
Int calc(int cup){
    //N0 -> 2  2 -> 0F(N)
    //F(N) = 3*F(N-1)+2
    //F(N)+1 = 3*F(N-1)+3
    //F(N)+1 = 3*(F(N-1)+1)
    //F(N)+1 = 3^(N-1)*(2+1)
    //F(N) = 3^N-1

    Int ret = 0;

    int move_dist = abs(Dist[cup] - crt);
    if (move_dist == 1){
        ret = 1;
        for (int i = 0; i < (N-cup); i++){
            ret *= 3LL;
        }
        crt = 2 - crt;    
    }else if(move_dist == 2){
        ret = 1;
        for (int i = 0; i < (N-cup); i++){
            ret *= 3LL;
        }
        ret--;
        ret *= 2;
        ret += 2;
    }
    return ret;
}

int solve(){
    cin >> N >> M;
    if (!N && !M) return 0;
    for (int i = 0; i < 3; i++){
        int n; cin >> n;
        for (int j = 0; j < n; j++){
            int cup; cin >> cup;
            Dist[cup] = i;
        }
    }

    crt = 0;
    Int ans[2] = {0}; 
    for (int i = 1; i <= N; i++){
        ans[0] += calc(i);
    }
    crt = 2;
    for (int i = 1; i <= N; i++){
        ans[1] += calc(i);
    }
    if (min(ans[0], ans[1]) > M) ans[0] = -1;
    cout << min(ans[0], ans[1]) << endl;
    return 1;
}


int main(){
    cin.tie(0);
    while (solve());
}

