int bfs(int m,vector<int>& root, map<string,int> visited){

	priority_queue<P,vector<P>,greater<P> > que;
	que.push(P(0,root));

	vector<int> tmp;
	vector<int> sets;
	while(!que.empty()){
		int cost = que.top().first;

		sets = que.top().second;

