            int j,k;

            scanf("%d", &k);
            for (j=0;j < k;j++) {
                int tmp;

                scanf("%d", &tmp);
                cond[tmp-1] = i;
            }
        }

        dist = (cond[0] != B)?cond[0]:A;

        for (i=0;i < n;i++) {
            move(i, dist);
        }

        if (sum <= m) {
