using namespace std;

typedef tuple<int, int, int> Tuple;
map<Tuple, int> S;
Tuple goal;
int n, m;

int get_last( int m ) {
    for ( int i = n - 1; i >= 0; -- i ) {
        if ( ( m & ( 1 << i ) ) == ( 1 << i ) ) return 1 << i;
    }
    return 0;
}

int remove_last( int m ) {
    for ( int i = n - 1; i >= 0; -- i ) {
        if ( ( m & ( 1 << i ) ) == ( 1 << i ) ) return m ^ ( 1 << i );
    }
    return m;
}

int solve( int A, int B, int C, int turns ) {
    Tuple tuple = make_tuple( A, B, C );
    if ( S.count( tuple ) > 0 && turns >= S[tuple] ) return -1;
    S[tuple] = turns;
    if ( turns > m ) return -1;
    if ( tuple == goal ) return turns;
    if ( A != 0 ) {
        int u = get_last(A);
        int v = get_last(B);
        if ( u > v ) {
            int ret = solve( remove_last( A ), B | u, C, turns + 1 );
            if ( ret != -1 ) return ret;
        }
    }
    if ( B != 0 ) {
        {
            int u = get_last(B);
            int v = get_last(A);
            if ( u > v ) {
                int ret = solve( A | u, remove_last(B), C, turns + 1 );
                if ( ret != -1 ) return ret;
            }
        }
        {
            int u = get_last(B);
            int v = get_last(C);
            if ( u > v ) {
                int ret = solve( A, remove_last(B), C | u, turns + 1 );
                if ( ret != -1 ) return ret;
            }
        }
    }
    if ( C != 0 ) {
        int u = get_last(C);
        int v = get_last(B);
        if ( u > v ) {
            int ret = solve( A, B | u, remove_last(C), turns + 1 );
            if ( ret != -1 ) return ret;
        }
    }
    return -1;
}

