  for(int i=n-1;i>=0;i--){
    for(int j=0;j<N;j++){
      if(s.t[j] & 1 << i) tmp[j] = max(tmp[j], i);
    }
  }
  for(int i=0;i<N;i++){
    if(tmp[i] != -1){
      for(int j=0;j<N;j++){
        if(abs(i-j) != 1) continue;
        if(tmp[i] > tmp[j]){
          S t = s;
          t.t[i] &= ~(1 << tmp[i]);
          t.t[j] |= 1 << tmp[i];
          t.cost++;
          res = min(res, calc(t, i, j));
          ans = min(ans, res);
        }
      }
    }
  }
  if(res == INF) return -1;
  return res;
}

