    for ( int i = n-1; i > 0; -- i ) P[i-1] = 3 * P[i] + 2;

    A[n-1] = T[n-1];
    B[n-1] = 2 - T[n-1];
    for ( int i = n; i > 0; -- i ) {
        if ( T[i-2] == 0 ) {
            A[i-2] = A[i-1];
