static const int MAX = 15000001;
map< vector<int>, int> memo;
int n,m;
int hanoi(stack<int> stqn[3], int phase){
  int ret = MAX, tpr;
  bool a=stqn[0].empty(), b=stqn[1].empty(), c=stqn[2].empty();
  if(b && (a||c)) return phase;
  if(phase==m) return -1;
  stack<int> tmp[3];
  vector<int> mem(15);
  REP(i,15) mem[i]=-1;
  REP(i,3) tmp[i]=stqn[i];
  while(!tmp[0].empty()){
    mem[tmp[0].top()] = 0;
    tmp[0].pop();
  }
  while(!tmp[1].empty()){
    mem[tmp[1].top()] = 1;
    tmp[1].pop();
  }
  while(!tmp[2].empty()){
    mem[tmp[2].top()] = 2;
    tmp[2].pop();
  }
  if(memo.find(mem)!=memo.end() && memo[mem]<=phase) return -1;
  memo[mem] = phase; 
  if(!a && (b||stqn[1].top()<stqn[0].top())){
    REP(i,3) tmp[i]=stqn[i];
    tmp[1].push(tmp[0].top());
    tmp[0].pop();
    if((tpr=hanoi(tmp, phase+1))!=-1){
      ret = min(ret, tpr);
    }
  }
  if(!b && (a||stqn[0].top()<stqn[1].top())){
    REP(i,3) tmp[i]=stqn[i];
    tmp[0].push(tmp[1].top());
    tmp[1].pop();
    if((tpr=hanoi(tmp, phase+1))!=-1){
      ret = min(ret, tpr);
    }
  }
  if(!b && (c||stqn[2].top()<stqn[1].top())){
    REP(i,3) tmp[i]=stqn[i];
    tmp[2].push(tmp[1].top());
    tmp[1].pop();
    if((tpr=hanoi(tmp, phase+1))!=-1){
      ret = min(ret, tpr);
    }
  }
  if(!c && (b||stqn[1].top()<stqn[2].top())){
    REP(i,3) tmp[i]=stqn[i];
    tmp[1].push(tmp[2].top());
    tmp[2].pop();
    if((tpr=hanoi(tmp, phase+1))!=-1){
      ret = min(ret, tpr);
    }
  }
  if(ret!=MAX) return ret;
  else return -1;
}
