using namespace std;

class state{
public:
  stack<int> tray[3];
  int cnt;
  int st;
  state(stack<int> a, stack<int> b, stack<int> c, int cnt){
    //tray = new stack<int>[3];
    tray[0] = a;
    tray[1] = b;
    tray[2] = c;
    this->cnt = cnt;
  }

  state(){
    //tray = new stack<int>[3];
    cnt = 0;
    st = 0;
  }

  state(const state &ob){
    //tray = new stack<int>[3];
    for(int i=0; i<3; i++) tray[i] = ob.tray[i];
    cnt = ob.cnt;
    st = ob.st;
  }

  bool operator < (const state& s) const {
    return st < s.st;
  }

  void init(){
    for(int i=0; i<3; i++){
      int n; cin >> n;
      int cup;
      for(int j=0; j<n; j++){
        cin >> cup;
        tray[i].push(cup);
        st += i*my_pow(cup);
      }
    }
  }

  //ij
  bool canChange(int i, int j){
    int sizei=tray[i].size(), sizej=tray[j].size();
    if(sizei==0) return false;
    if(sizej==0) return true;

    if(tray[i].top()>tray[j].top()) return true;
    else return false;
  }

  //ijstate
  state change(int i, int j){
    state ex = state(tray[0], tray[1], tray[2], cnt+1);
    int cup=ex.tray[i].top(); ex.tray[i].pop();
    ex.tray[j].push(cup);

    ex.st += (-i+j)*my_pow(cup);

    return ex;
  }
private:
  //return pow(3, n)
  int my_pow(int n){
    int ans=1;
    for(int i=0; i<n; i++) ans *= 3;
    return ans;
  }
};

int solve(int, int, state);

int main(){
  int n, m; // 
  while(cin >> n >> m, n!=0 && m!=0){
    state first;
    first.init();
    int dis=solve(n, m, first);
    cout << dis << endl;
  }

  return 0;
}

int solve(int n, int m, state first){
  queue<state> q;
  q.push(first);
  map<state, bool> mp;

  int ans=-1;

  while(!q.empty()){
    state now = q.front(); q.pop();
    //-1
    if(now.cnt>m){
      ans = -1;
      break;
    }

    //
    if(now.tray[0].size()==n || now.tray[2].size()==n){
      ans = now.cnt;
      break;
    }

    //2
    for(int i=0; i<2; i++){
      if(now.canChange(i, i+1)) {
        state next = now.change(i, i+1);
        //continue
        if(mp[next]) continue;
        mp[next] = true;
        q.push(next);
      }
      if(now.canChange(i+1, i)){
        state next = now.change(i+1, i);
        //continue
        if(mp[next]) continue;
        mp[next] = true;
        q.push(next);
      }
    }
  }

  return ans;
}

