int dp[10000000];
int N, M;
int to_hash(const vector<int>&nums) {
	int hash = 0;
	for (auto n : nums) {
		hash = 3 * hash + n;
	}
	return hash;
}
int getans(vector<int>&nums, int now) {
	int hash = to_hash(nums);
	if (dp[hash] == -2) {
		return M;
	}
	if (dp[hash] == -1) {
		if (now >= M)return M;
		if (to_hash(nums) == 0) {
			return now;
		}
		else {
			dp[hash] = -2;
			vector<int>tops(3, -1);
			for (int i = 0; i < N; ++i) {
				tops[nums[i]] = i;
			}
			int ans = M;
			if (tops[0] < tops[1]) {
				if (tops[1] != -1) {
					nums[tops[1]] = 0;
					ans = min(ans, getans(nums, now + 1));
					nums[tops[1]] = 1;
				}
			}
			else {
				if (tops[0] != -1) {
					nums[tops[0]] = 1;
					ans = min(ans, getans(nums, now + 1));
					nums[tops[0]] = 0;
				}
			}
			if (tops[1] < tops[2]) {
				if (tops[2] != -1) {
					nums[tops[2]] = 1;
					ans = min(ans, getans(nums, now + 1));
					nums[tops[2]] = 2;
				}
			}
			else {
				if (tops[1] != -1) {
					nums[tops[1]] = 2;
					ans = min(ans, getans(nums, now + 1));
					nums[tops[1]] = 1;
				}
			}
			return dp[hash] = ans;
		}
	}
	else {
		return dp[hash];
	}

}

int main() {
	while (1) {
		for (int i = 0; i < 10000000; ++i) {
			dp[i] = -1;
		} cin >> N >> M;
		if (!N)break;
		vector<int>nows(N);
		for (int i = 0; i < 3; ++i) {
			int a; cin >> a;
			for (int j = 0; j < a; ++j) {
				int b; cin >> b; b--;
				nows[b] = i;
			}
		}
		int ans = getans(nows, 0);
		if (ans == M)ans = -1;
		cout << ans << endl;
	}

	return 0;
}

