typedef long long ll;

ll Pow3T[16];
ll Pow3(int n)
{
    if (Pow3T[n] != 0) return Pow3T[n];
    if (n == 0) return Pow3T[n]=1;
    else if (n==1) return Pow3T[n]=3;
    else if (n%2==0) {
        ll Tmp = Pow3(n/2);
        return Pow3T[n]=Tmp*Tmp;
    } else {
        return Pow3T[n]=3 * Pow3(n-1);
    }
}

int main()
{
