        while (!Q.empty()) {
            ll Now = Q.front(); Q.pop();
            vector<ll> vNext;

            {
                int k = N-1;
                while (Now/Pow3(N-1) == (Now/Pow3(k))%3) {--k; if (k==-1) break;}
                if (k == -1) {
                    if (Now%3 == 1) ;
                    else {
                        Res = D[Now];
                        break;
                    }
                }

                ll Tmp = Now;
                if (Now/Pow3(N-1) == 0) {
                    Tmp = Now + Pow3(N-1);
                    vNext.push_back(Tmp);
                    if ((Now/Pow3(k))%3 == 1) {
                        Tmp = Now + Pow3(k);
                        vNext.push_back(Tmp);
                    } else if ((Now/Pow3(k))%3 == 2) {
                        Tmp = Now - Pow3(k);
                        vNext.push_back(Tmp);
                    }
                }
                if (Now/Pow3(N-1) == 1) {
                    Tmp = Now - Pow3(N-1);
                    vNext.push_back(Tmp);
                    Tmp = Now + Pow3(N-1);
                    vNext.push_back(Tmp);
                }
                if (Now/Pow3(N-1) == 2) {
                    Tmp = Now - Pow3(N-1);
                    vNext.push_back(Tmp);
                    if ((Now/Pow3(k))%3 == 0) {
                        Tmp = Now + Pow3(k);
                        vNext.push_back(Tmp);
                    } else if ((Now/Pow3(k))%3 == 1) {
                        Tmp = Now - Pow3(k);
                        vNext.push_back(Tmp);
                    }
                }

                int d = D[Now] + 1;
                if (d > M) continue;
                for (int i = 0; i < vNext.size(); ++i) {
                    ll TmpS = vNext[i];
                    if (D[TmpS] == -1) {
                        D[TmpS] = d;
                        Q.push(TmpS);
