std::stack<int> trays[3];
std::stack<int> r_trays[3];

void moveTop(int i, int j) {
  // move top value i to j
  trays[j].push(trays[i].top());
  trays[i].pop();
}

void clearTray() {
  for (int i = 0; i < 3; i ++) {
    while(!trays[i].empty())
      trays[i].pop();
    while(!r_trays[i].empty())
      r_trays[i].pop();
  }
}

bool isEmpty() {
  if((trays[0].empty() && trays[1].empty()) || (trays[1].empty() && trays[2].empty()))
    return true;
  else
    return false;
}

int moveCups(int m) {
  int move = 0;
  bool flag = true;

  if (isEmpty())
    return 0;

  while(flag) {
    for (int i = 0; i < 2; i ++) { 
      move ++;
      // move bigger cup
      if (trays[i + 1].empty() || (!trays[i].empty() && trays[i].top() > trays[i + 1].top())) {
        moveTop(i, i + 1); 
      } else {
        moveTop(i + 1, i);
      }
      // check situation
      if (isEmpty()) {
        flag = false;
        break;
      } else if (move >= m) {
        flag = false;
        move = -1;
        break;
      }
    }
  }
  return move;
}

int main() {
  int n, m;
  int num, temp, move, left, right;
  bool flag = true;
  while (1) {
    std::cin >> n >> m;
    if (n == 0)
      break;
    // store cups to trays.
    for (int i = 0; i < 3; i ++) {
      std::cin >> num;
      for (int j = 0; j < num; j ++) {
        std::cin >> temp;
        trays[i].push(temp);
      }
      r_trays[2 - i] = trays[i];
    }
    std::reverse(std::begin(r_trays), std::end(r_trays));
    right = moveCups(m);
    for (int i = 0; i < 3; i ++)
      trays[i].swap(r_trays[2 - i]);
    left = moveCups(m);

    std::cout << (right > left ? right : left) << std::endl;
    clearTray();
  }
  return 0;
}

#include <iostream>
#include <stack>

typedef std::stack<int> Tray;

bool moveNtoN(Tray tr[], int src, int dst){
    if((tr[src]).empty() ||
        !(tr[dst]).empty() && (tr[src]).top() < (tr[dst]).top()){
        return false;
    }
    (tr[dst]).push((tr[src]).top());
    (tr[src]).pop();
    return true;
}

bool ifSorted(Tray tr[]){
    return ((tr[1]).empty() && ((tr[0]).empty() || (tr[2].empty())));
}

int solve(Tray argTr[], int m){
    if(ifSorted(argTr)){
        return 0;
    }
    Tray tr[3];
    int lastMove;          // 0 : A->B, 1 : B->A, 2 : B->C, 3 : C->B
    for(int j = 0; j < 2; j++){
        tr[0] = argTr[0];
        tr[1] = argTr[1];
        tr[2] = argTr[2];
        lastMove = -1;
        if(j == 0){
            if(moveNtoN(tr, 0, 1)){ 
                lastMove = 0;
            }
            else if(moveNtoN(tr, 1, 0)){
                lastMove = 1;
            }
        }
        else {
            if(moveNtoN(tr, 1, 2)){
                lastMove = 2;
            }
            else if(moveNtoN(tr, 2, 1)){
                lastMove = 3;
            }
        }
        for(int i = 1; i < m; i++){
            if(ifSorted(tr)){
                return i;
            }
            if(lastMove != 1 && moveNtoN(tr, 0, 1)){
                lastMove = 0;
            }
            else if(lastMove != 0  && moveNtoN(tr, 1, 0)){
                lastMove = 1;
            }
            else if(lastMove != 3 && moveNtoN(tr, 1, 2)){
                lastMove = 2;
            }
            else if(lastMove != 2 && moveNtoN(tr, 2, 1)){
                lastMove = 3;
            }
        }
        if(ifSorted(tr)){
            return m;
        }
    }
    return -1;
}

int main(){
    int n, m;
    while(1){
        std::cin >> n >> m;
        if(!(n || m)){
            break;
        }
        Tray trays[3];
        for(int col = 0; col < 3; col++){
            int nCups;                     // #cups on the line
            std::cin >> nCups;
            while(nCups--){
                int cup;
                std::cin >> cup;
                (trays[col]).push(cup);
            }
        }
        std::cout << solve(trays, m) << std::endl;
    }
    return 0;
}

