#include <iostream>
#include <tuple>
#include <map>
using namespace std;

const int NONE = -1;
typedef tuple<int, int, int> Tuple;
map<Tuple, int> S;
Tuple goal;
int n, m;

int get_last( int m ) {
    for ( int i = n - 1; i >= 0; -- i ) {
        if ( ( m & ( 1 << i ) ) == ( 1 << i ) ) return 1 << i;
    }
    return 0;
}

int remove_last( int m ) {
    for ( int i = n - 1; i >= 0; -- i ) {
        if ( ( m & ( 1 << i ) ) == ( 1 << i ) ) return m ^ ( 1 << i );
    }
    return m;
}

int solve( int A, int B, int C, int turns ) {
    Tuple tuple = make_tuple( A, B, C );
    if ( S.count( tuple ) > 0 && turns >= S[tuple] ) return -1;
    S[tuple] = turns;
    if ( turns > m ) return -1;
    if ( tuple == goal ) return turns;
    if ( A != 0 ) {
        int u = get_last(A);
        int v = get_last(B);
        if ( u > v ) {
            int ret = solve( remove_last( A ), B | u, C, turns + 1 );
            if ( ret != NONE ) return ret;
        }
    }
    if ( B != 0 ) {
        int u = get_last(B);
        int v = get_last(A);
        if ( u > v ) {
            int ret = solve( A | u, remove_last(B), C, turns + 1 );
            if ( ret != NONE ) return ret;
        }
    }
    if ( B != 0 ) {
        int u = get_last(B);
        int v = get_last(C);
        if ( u > v ) {
            int ret = solve( A, remove_last(B), C | u, turns + 1 );
            if ( ret != NONE ) return ret;
        }
    }
    if ( C != 0 ) {
        int u = get_last(C);
        int v = get_last(B);
        if ( u > v ) {
            int ret = solve( A, B | u, remove_last(C), turns + 1 );
            if ( ret != NONE ) return ret;
        }
    }
    return -1;
}

int main() {
    while ( cin >> n >> m ) {
        if ( n == 0 && m == 0 ) break;
        S.clear();
        int T[3] = { 0, 0, 0 };
        for ( int i = 0; i < 3; ++ i ) {
            int k;
            cin >> k;
            for ( int j = 0; j < k; ++ j ) {
                int t;
                cin >> t;
                t--;
                T[i] |= 1 << t;
            }
        }
        goal = make_tuple( T[0], T[1], T[2] );
        int ret1 = solve( (1<<n)-1, 0, 0, 0 );
        int ret2 = solve( 0, 0, (1<<n)-1, 0 );
        if ( ret1 == NONE && ret2 == NONE ) {
            cout << -1 << endl;
        } else if ( ret1 != NONE && ret2 != NONE ) {
            cout << min( ret1, ret2 ) << endl;
        } else if ( ret1 != NONE ) {
            cout << ret1 << endl;
        } else if ( ret2 != NONE ) {
            cout << ret2 << endl;
        }
    }
    return 0;
}

