int n, m;
stack<int> stk[3];

void input() {
	int i;
	for (i = 0; i < 3; i++) { while (!stk[i].empty()) stk[i].pop(); }
	for (i = 0; i < 3; i++) {
		int num, value;
		cin >> num;
		for (int j = 0; j < num; j++) {
			cin >> value;
			stk[i].push(value);
		}
	}
}

bool operate(stack<int> *s, int &pos) {
	if (s[pos].empty() && s[pos + 1].empty()) return false;
	else if (s[pos].empty()) { s[pos].push(s[pos + 1].top()); s[pos + 1].pop(); }
	else if (s[pos + 1].empty()) { s[pos + 1].push(s[pos].top()); s[pos].pop(); }
	else if (s[pos].top() < s[pos + 1].top()) { s[pos].push(s[pos + 1].top()); s[pos + 1].pop(); }
	else { s[pos + 1].push(s[pos].top()); s[pos].pop(); }
	pos = !pos;
	return true;
}

int simurate(int pos) {
	int i;
	stack<int> s[3];

	for (i = 0; i < 3; i++) s[i] = stk[i];
	for (i = 0; i <= m; i++) {
		if (s[0].size() == n || s[2].size() == n) return i;
		if (!operate(s, pos)) return m + 1;
	}
	return i;
}

int main() {
	while (cin >> n >> m) {
		if (!n) break;
		input();
		int ans = min(simurate(0), simurate(1));
		if (ans <= m) { cout << ans << endl; }
		else { cout << -1 << endl; }
	}
	return 0;
}

