int main(){
  int n,m,now;

  for(;;){
    cin >> n >> m;
    if(!n && !m)break;

    now = 0;
    for(int i=0;i<3;i++){
      int num;
      cin >> num;
      for(int j=0;j<num;j++){
	int tmp;
	cin >> tmp;
	now += pow(3,tmp-1)*i;
      }
    }

    int lim = pow(3,n);
    for(int i=0;i<lim;i++)use[i] = false;
    use[now] = true;

    int ans = -1;

    queue<P> q;
    q.push(P(now,0));

    while(q.size()){
      P p = q.front();q.pop();
      stack<int> s[3];
      now = p.first;
      //cout << now << endl;
      for(int i=1;i<=n;i++){
	s[now%3].push(i);
	now/=3;
      }

      if((int)s[0].size()==n || (int)s[2].size()==n){
	ans = p.second;
	break;
