int rec(int a, int b, int c){
	if(!a&&!b) return 0;
	int ans = 0;
	if(c&1) ans = rec(a>>1, b>>1, c>>1);
	else if(b&1) ans = rec(c>>1, b>>1, a>>1) + rec((a|b|c)>>1, 0, 0) + 1;
	else if(a&1) ans = rec(a>>1, b>>1, c>>1) + 2*rec((a|b|c)>>1, 0, 0) + 2;
	return ans;
}

