bool move(std::vector<std::stack<char> > &tray, int i, int j){
	if(tray[i].empty()){
		return false;
	}
	char c = tray[i].top();

	if(!tray[j].empty()){
		char cc = tray[j].top();

		if(c < cc){
			return false;
		}
	}

	tray[i].pop();
	tray[j].push(c);
	return true;
