int n, m;
int POW[16];

int calc(int A, int B, int C) {
	int res = 0;
	for_(i,0,n) {
		int k = n - i - 1;
		if (A >> i & 1) res += 2 * POW[k], swap(A, C);
		if (B >> i & 1) res += POW[k];
	}
	return res;
}

void solve() {
	int cup[3] = { 0 };

	for_(i,0,3) {
		int d; cin >> d;
		for_(j,0,d) {
			int p; cin >> p; --p;
			cup[i] |= 1 << p;
		}
	}

	int ans = min(calc(cup[0], cup[1], cup[2]), calc(cup[2], cup[1], cup[0]));
	if (ans > m) ans = -1;
	cout << ans << endl;
}

