const int INF = 1 << 28;
int n,m,ans;

void rec(int A, int B, int C, int k) {
  if (k == m) return;
  if (A == (1<<n)-1 || C == (1<<n)-1) ans = min(ans,k);
  int ta = -1, tb = -1, tc = -1;
  rep (i,n) {
    if (A >> i & 1) ta = i;
    if (B >> i & 1) tb = i;
    if (C >> i & 1) tc = i;
  }
  if (A != 0 && ta > tb) rec(A & ~(1<<ta), B | (1<<ta), C, k+1);
  if (B != 0) {
    if (tb > ta) rec(A | (1<<tb), B & ~(1<<tb), C, k+1);
    if (tb > tc) rec(A, B & ~(1<<tb), C | (1<<tb), k+1);
  }
  if (C != 0 && tc > tb) rec(A, B | (1<<tc), C & ~(1<<tc), k+1);
}

