#include<bits/stdc++.h>
using namespace std;
int bit(int a,int b,int c){
    if(!a & !b){
        return 0;
    }
    if(c & 1){
        return (bit(a>>1,b>>1,c>>1));
    }
    if(b & 1){
        return (bit(c>>1,b>>1,a>>1)+bit((a|b|c)>>1,0,0)+1);
    }
    if(a & 1){
        return (bit(a>>1,b>>1,c>>1)+2*bit((a|b|c)>>1,0,0)+2);
    }
}
int main(void){
    int n,m,data[3];
    while(1){
        cin>>n>>m;
        if(n==0 && m==0){
            return 0;
        }
        for(int i=0;i<3;i++){
            int k;
            cin>>k;
            data[i]^=data[i];
            for(int j=0;j<k;j++){
                int num;
                cin>>num;
                data[i]|=1 << --num;
            }
        }
        int ans=min(bit(data[0],data[1],data[2]),bit(data[2],data[1],data[0]));
        if(ans<=m){
            cout<<ans<<endl;
        }else{
            cout<<-1<<endl;
        }
    }
}

