using namespace std;

int main() {
  int input, height, i, j;
  int n, m;
  int turns_left, turns_right;
  int position, d;

  while (true){
    cin >> n;
    cin >> m;

    if (n == 0 && m == 0) { break; }

    vector<int> cups(15);

    // input
    for (i = 0; i < 3; i++) {
      cin >> height;
      for (j = 0; j < height; j++) {
        cin >> input;
        cups.at(input - 1) = i;
      }
    }

    // left
    position = 0;
    turns_left = 0;
    for (i = 0; i < n; i++) {
      d = position - cups.at(i);
      if (d == 0) {
      } else if (d == 1 || d == -1) {
        turns_left += static_cast<int>(pow(3, (n - 1) - i));
        if (position == 0) {
          position = 2;
        } else if (position == 2) {
          position = 0;
        }
      } else if (d == 2 || d == -2) {
        turns_left += 2 * static_cast<int>(pow(3, (n - 1) - 1));
      }
    }

    // right
    position = 2;
    turns_right = 0;
    for (i = 0; i < n; i++) {
      d = position - cups.at(i);
      if (d == 0) {
      } else if (d == 1 || d == -1) {
        turns_right += static_cast<int>(pow(3, (n - 1) - i));
        if (position == 0) {
          position = 2;
        } else if (position == 2) {
          position = 0;
        }
      } else if (d == 2 || d == -2) {
        turns_right += 2 * static_cast<int>(pow(3, (n - 1) - 1));
      }
    }

    cout << (min(turns_left, turns_right) <= m ? min(turns_left, turns_right) : -1) << endl;

    cups.clear();
  }

  return 0;
}

#include<iostream>
#include<algorithm>
using namespace std;


int n, m, cnt, a[16], b[16];
bool flag;

void move(int num, int dest, int* a);

int main() {

	while (1) {
		cin >> n >> m;
		if (n == 0 && m == 0)break;

		int numt, tmp;
		for (int i = 0; i < 3; i++) {
			cin >> numt;
			for (int j = 0; j < numt; j++) {
				cin >> tmp;
				a[tmp] = i;
				b[tmp] = i;
			}
		}

		int ans1, ans2;

		cnt = 0;
		flag = true;
		for (int num = 1; num <= n; num++) {
			move(num, 2, a);
		}
		if (flag == true) {
			ans1 = cnt;
		} else {
			ans1 = -1;
		}

		cnt = 0;
		flag = true;
		for (int num = 1; num <= n; num++) {
			move(num, 0, b);
		}
		if (flag == true) {
			ans2 = cnt;
		} else {
			ans2 = -1;
		}


		int ans;
		if (ans1 == -1) {
			ans = ans2;
		} else if(ans2 == -1){
			ans = ans1;
		} else {
			ans = min(ans1, ans2);
		}
		//cout << ans1 << " " << ans2 << endl;
		cout << ans << endl;
	}


	return 0;
}

void move(int num, int dest, int* a) {

	if (cnt > m || flag == false) {
		flag = false;
		return;
	}

	int dist = abs(dest - a[num]);
	if (dist == 0) {
		return;
	} else if (dist == 1) {
		if (num == n) {
			cnt += 1;
			a[num] = dest;
		} else {
			int i = 1;
			while (num + i <= n) {
				move(num + i, 3 - dest - a[num], a);
				i++;
			}
			cnt += 1;
			a[num] = dest;
		}
	} else if (dist == 2) {
		if (num == n) {
			cnt += 2;
			a[num] = dest;
		} else {
			move(num, 1, a);
			move(num, dest, a);
		}
	}


	return;
}

