	while(cin>>x>>n,n){
		for(i=0;i<3;i++)for(cin>>m;m--;h[t]=i)cin>>t;
		a[x]=h[x];c[x]=2-h[x];
		for(i=x;i>1;i--){
			if(h[i-1]==0){
				a[i-1]=a[i];
				c[i-1]=c[i]+2+4*S(i);
			}else if(h[i-1]==1){
				a[i-1]=c[i]+1+2*S(i);
				c[i-1]=a[i]+1+2*S(i);
			}else{
				a[i-1]=a[i]+2+4*S(i);
				c[i-1]=c[i];
			}
