// Hc(A,B,C) :=
//  - minCup ??? C -> Hc(A,B,C-{minCup})
//  - minCup ??? B -> Ha(A,B-{minCup},C)+1+Hc(A???(B-{minCup}???C,0,0)
//  - minCup ??? A -> Hc(A-{minCup},B,C)+1+Ha(0,0,(A-{minCup}???B???C))+1+Hc((A-{minCup})???B???C,0,0)
// Ha(A,B,C) == Hc(C,B,A)

int solve(int A, int B, int C) {
  if(A == 0 && B == 0) return 0;
  if(C&1) return solve(A>>1, B>>1, C>>1);
  if(B&1) return solve(C>>1, B>>1, A>>1) + solve((A|B|C)>>1, 0, 0) + 1;
  if(A&1) return solve(A>>1, B>>1, C>>1) + 2*(solve((A|B|C)>>1, 0, 0)+1);
}

