#define NOT 100000000

using namespace std;

int N, M;

map<vector<stack<int> >, int> mp;

int search(vector<stack<int> > A, int n)
{
  //cout << "ok" << endl;
  if  (A[0].size() == N || A[2].size() == N) {
    return 0;
  }
  if (n == M) {
    return NOT;
  }
  int m = NOT;
  if (A[0].size() != 0 && (A[1].size() == 0 || (A[0].top() > A[1].top()))) {
    vector<stack<int> > _A = A;
    _A[1].push(_A[0].top());
    _A[0].pop();
    int t;
    if (mp.find(_A) == mp.end()) {
      t = search(_A, 0);
      mp[_A] = t;
    } else {
      t = mp[_A];
    }
    m = min(m, t + 1);
  }
  if (A[1].size() != 0 && (A[2].size() == 0 || (A[1].top() > A[2].top()))) {
    vector<stack<int> > _A = A;
    _A[2].push(_A[1].top());
    _A[1].pop();
    int t;
    if (mp.find(_A) == mp.end()) {
      t = search(_A, 0);
      mp[_A] = t;
    } else {
      t = mp[_A];
    }
    m = min(m, t + 1);
  }
  if (A[1].size() != 0 && (A[0].size() == 0 || (A[1].top() > A[0].top()))) {
    vector<stack<int> > _A = A;
    _A[0].push(_A[1].top());
    _A[1].pop();
    int t;
    if (mp.find(_A) == mp.end()) {
      t = search(_A, 0);
      mp[_A] = t;
    } else {
      t = mp[_A];
    }
    m = min(m, t + 1);
  }
  if (A[2].size() != 0 && (A[1].size() == 0 || (A[2].top() > A[1].top()))) {
    vector<stack<int> > _A = A;
    _A[1].push(_A[2].top());
    _A[2].pop();
    int t;
    if (mp.find(_A) == mp.end()) {
      t = search(_A, 0);
      mp[_A] = t;
    } else {
      t = mp[_A];
    }
    m = min(m, t + 1);
  }
  return m;
}

int main()
{
  while (scanf("%d %d ", &N, &M) == 2) {
    vector<stack<int> >A(3);
    //mp.clear();
    if (N == 0 && M == 0) {
      break;
    }
    for (int i = 0; i < 3; i++) {
      int c;
