LL j3[16];
int cp[20];
LL L(int s, int e);
LL C(int s, int e);
LL R(int s, int e);

inline LL f(int k){
	return j3[k]-1;
}

inline LL g(int k){
	return (j3[k]-1)/2;
}

LL L(int s, int e){
	if(s==e)return cp[s];
	switch(cp[s]){
		case 0:
			return L(s+1, e);
		case 1:
			return min(L(s+1, e)+g(e-s)+g(e-s+1), min(C(s+1,e)+g(e-s+1), R(s+1, e)+1+f(e-s)));
		case 2:
			return min(L(s+1, e)+1+g(e-s)+g(e-s+1), min(C(s+1, e)+min(g(s-e)+f(s-e+1), 2*g(s-e)+1+g(s-e+1)), R(s+1, e)+f(e-s+1)));
	}	
}

LL C(int s, int e){
	if(s==e){
		if(cp[s]==1)return 0;
		else return 1;
	}
	switch(cp[s]){
		case 0:
			return min(L(s+1, e)+g(e-s+1), min(C(s+1, e)+2*g(e-s)+1,  R(s+1, e)+1+g(e-s)));
		case 1:
			return C(s+1, e);
		case 2:
			return min(L(s+1, e)+1+g(e-s), min(C(s+1, e)+2*g(e-s)+1, R(s+1, e)+g(e-s+1)));
	}	
}

LL R(int s, int e){
	if(s==e)return 2-cp[s];
	switch(cp[s]){
		case 2:
			return R(s+1, e);
		case 1:
			return min(R(s+1, e)+g(e-s)+g(e-s+1), min(C(s+1,e)+g(e-s+1), L(s+1, e)+1+f(e-s)));
		case 0:
			return min(R(s+1, e), min(C(s+1, e)+min(g(s-e)+f(s-e+1), 2*g(s-e)+1+g(s-e+1)), L(s+1, e)+f(e-s+1)));
	}	
}


