        if((A.size() > 0 && B.size() > 0 && A.back() > B.back()) || (A.size() > 0 && B.size() == 0)){
            S[A.back()] = 'B';
            if(already.count(change(S)) == 0){
                already.insert(change(S));
                que.push(MP(INT_MAX-c-1, change(S)));
            }
            S[A.back()] = 'A';
        }
        if((A.size() > 0 && B.size() > 0 && A.back() < B.back()) || (A.size() == 0 && B.size() > 0)){
            S[B.back()] = 'A';
            if(already.count(change(S)) == 0){
                already.insert(change(S));
                que.push(MP(INT_MAX-c-1, change(S)));
            }
            S[B.back()] = 'B';
        }
        if((C.size() > 0 && B.size() > 0 && C.back() > B.back()) || (C.size() > 0 && B.size() == 0)){
            S[C.back()] = 'B';
            if(already.count(change(S)) == 0){
                already.insert(change(S));
                que.push(MP(INT_MAX-c-1, change(S)));
            }
            S[C.back()] = 'C';
        }
        if((C.size() > 0 && B.size() > 0 && C.back() < B.back()) || (C.size() == 0 && B.size() > 0)){
            S[B.back()] = 'C';
            if(already.count(change(S)) == 0){
                already.insert(change(S));
                que.push(MP(INT_MAX-c-1, change(S)));
            }
            S[B.back()] = 'B';
        }

        que.pop();
        if(que.empty()){
            cout << -1 << endl;
            break;
        }
    }

    while(!que.empty()) que.pop();

