#include <bits/stdc++.h>
using namespace std;

int topCup(int tray)
{
    int count = 0;
    while (tray > 0)
    {
        tray = tray >> 1;
        count++;
    }
    return count;
}

uint64_t trays2ll(const array<uint16_t, 3> &trays)
{
    return ((uint64_t)trays[0] << 32) + ((uint64_t)trays[1] << 16) + ((uint64_t)trays[3] << 0);
}

constexpr int list_from[] = {0, 1, 1, 2};
constexpr int list_to[] = {1, 0, 2, 1};

int solve(const array<uint16_t, 3> &trays, int m)
{
    //  
    unordered_set<uint64_t> seen;

    // 
    queue<int> task_cost;
    queue<array<uint16_t, 3>> task;

    task.push(trays);
    task_cost.push(0);

    int ans = -1;

    while (!task.empty())
    {
        const int now_m = task_cost.front();
        task_cost.pop();
        const auto now_trays = task.front();
        task.pop();

        // #ifdef DEBUG
        //         cerr << "search tray" << now_m << endl
        //              << bitset<6>(now_trays[0]) << endl
        //              << bitset<6>(now_trays[1]) << endl
        //              << bitset<6>(now_trays[2]) << endl
        //              << endl;
        // #endif

        if (now_m >= m)
            continue;

        // AC
        if ((now_trays[0] == 0 || now_trays[2] == 0) && now_trays[1] == 0)
        {
            ans = now_m;
            break;
        }

        // fromto
        for (int i = 0; i < 4; i++)
        {
            const int from = list_from[i], to = list_to[i];
            const int top_from = topCup(now_trays[from]), top_to = topCup(now_trays[to]);
            // from or tofrom  
            if (top_from == 0 || top_to > top_from)
                continue;

            array<uint16_t, 3> next_trays = now_trays;
            const int cup_moved = 1 << (top_from - 1);
            next_trays[to] += cup_moved;
            next_trays[from] -= cup_moved;

            if (seen.count(trays2ll(next_trays)))
                continue;

            task_cost.push(now_m + 1);
            task.push(next_trays);
            seen.insert(trays2ll(next_trays));
        }
    }

    return ans;
}

signed main()
{
    cin.tie(0);
    ios::sync_with_stdio(0);
    cout << setprecision(10);

    int n, m;
    while (cin >> n >> m)
    {
        if (n == 0)
            continue;
        array<uint16_t, 3> trays{};
        for (auto &&tray : trays)
        {
            int n_cup;
            cin >> n_cup;
            for (int i = 0; i < n_cup; i++)
            {
                int tmp;
                cin >> tmp;
                tray |= 1 << (tmp - 1);
            }
        }
        cout << dec << solve(trays, m) << endl;
    }
}

