using namespace std;

using State=vector<stack<int>>;
struct Scene{
	State state;
	int prev_from;
	int prev_to;
	int count;
	Scene(State state, int prev_from, int prev_to, int count) : 
		state(state), prev_from(prev_from), prev_to(prev_to), count(count){}
};

void enqueue(queue<Scene>& scene, Scene& s, int from, int to){
	if(s.prev_from==to && s.prev_to==from) return;
	if(s.state[from].empty()) return;
	if(!s.state[to].empty() && s.state[from].top()<s.state[to].top()) return;
	State t;
	copy(s.state.begin(), s.state.end(), back_inserter(t));
	t[to].push(t[from].top()); t[from].pop();
	scene.push(Scene(t, from, to, s.count+1));
}

void dump(Scene& s){
	cout << "--------------------------------------------------\n";
	cout << s.prev_from << "|" << s.prev_to << "|" << s.count << "\n";
	State t;
	copy(s.state.begin(), s.state.end(), back_inserter(t));
