int solve(State init, int m){
	queue<Scene> scene;
	scene.push(Scene(init, -1, -1, 0));
	while(!scene.empty()){
		auto s=scene.front();
		//dump(s);
		if(s.state[1].empty() && (s.state[0].empty() || s.state[2].empty())){
