#include <bits/stdc++.h>
using namespace std;
using ll = long long;
using Game = vector<char>;
using Tops = map<char, int>;
const char ts[3] = {'a', 'b', 'c'};
struct Pos {
public:
    int hash;
    Game g;
    int rank;
    Pos(int h, Game g, int rank) : hash(h), g(g), rank(rank) {};
    bool is_goal() {
        char f = g[0];
        bool ok = f == 'a' || f == 'c';
        if (!ok) return false;
        for (int i=1;i<g.size();++i) {
            if (f!=g[i]) return false;
        }
        return true;
    }
    Tops get_tops() {
        Tops ts {{'a', -1}, {'b', -1}, {'c', -1}};
        for (int i = 0; i < g.size(); ++i) {
            char t = g[i];
            ts[t] = i;
        }
        return ts;
    }
};

int to_hash(Game g) {
    int v = 0;
    for (char t: g) {
        int r = ('a' == t) ? 0 : ('b' == t) ? 1 : 2;
        v = v * 3 + r;
    }
    return v;
}

void solve(int N, ll M) {
    Game game(N);
    for (int i = 0; i < 3; ++i) {
        int n;
        cin >> n;
        char t = (i == 0) ? 'a' : (i == 1) ? 'b' : 'c';
        for (int j = 0; j < n; ++j) {
            int x;
            cin >> x;
            game[x - 1] = t;
        }
    }
    vector<char> foots(3500000, 'a');
    queue<Pos> q;
    q.emplace(to_hash(game), game, 0);
    int ans = -1;
    while (!q.empty()) {
        Pos pos = q.front();
        q.pop();
        if (foots[pos.hash] != 'a') continue;
        foots[pos.hash] = 'b';
        if (pos.is_goal()) {
            if (pos.rank <= M) ans = pos.rank;
            break;
        }
        Tops tops = pos.get_tops();
        int next_rank = pos.rank + 1;
        // a -> b
        if (tops['b'] < tops['a']) {
            Game a_b = pos.g;
            a_b[tops['a']] = 'b';
            q.emplace(to_hash(a_b), a_b, next_rank);
        }
        // b -> a
        if (tops['a'] < tops['b']) {
            Game b_a = pos.g;
            b_a[tops['b']] = 'a';
            q.emplace(to_hash(b_a), b_a, next_rank);
        }
        // b -> c
        if (tops['c'] < tops['b']) {
            Game b_c = pos.g;
            b_c[tops['b']] = 'c';
            q.emplace(to_hash(b_c), b_c, next_rank);
        } else {
            Game c_b = pos.g;
            c_b[tops['c']] = 'b';
            q.emplace(to_hash(c_b), c_b, next_rank);
        }
    }
    cout << ans << endl;
}

int main() {
    int n;
    ll m;
    while(true) {
        cin >> n >> m;
        if (n == 0 && m == 0) break;
        else solve(n, m);
    }
    return 0;
}

