inline int bitCount(int i) {
	i = i - ((i >> 1) & 0x55555555);
	i = (i & 0x33333333) + ((i >> 2) & 0x33333333);
	i = (i + (i >> 4)) & 0x0f0f0f0f;
	i = i + (i >> 8);
	i = i + (i >> 16);
	return i & 0x3f;
}

inline int MSB32bit(int v) {
	if (v == 0) return -1;
	v |= (v >> 1);
	v |= (v >> 2);
	v |= (v >> 4);
	v |= (v >> 8);
	v |= (v >> 16);
	return bitCount(v) - 1;
}

inline void movebit(int& a, int& b)
{
	int msb_a = MSB32bit(a);
	int msb_b = MSB32bit(b);
	if(msb_a > msb_b)
	{
		a -= (1 << msb_a);
		b += (1 << msb_a);
	}
	else if(msb_a == msb_b)
		return;
	else
	{
		a += (1 << msb_b);
		b -= (1 << msb_b);
	}
}

