		cin >> csize;
		for (int i = 0; i < csize; i++)
		{
			cs |= (1 << (getInt() - 1));
		}

		//a, b, c, count
		//a->b:0 b->a:1 b->c:2 c->b:3
		queue<tuple<int, int, int, int>> que;
		map<tuple<int, int, int>, bool> visited;
		que.push(tpl(as, bs, cs, 0));
		int ans = -1;
		while (!que.empty())
		{
			auto now = que.front(); que.pop();
			int a = get<0>(now);
			int b = get<1>(now);
			int c = get<2>(now);
			int count = get<3>(now);
			if(visited[tpl(a, b, c)]) continue;
			visited[tpl(a, b, c)] = true;
			if((a == 0 && b == 0) || (b == 0 && c == 0))
			{
				ans = count;
				break;
			}
			if(count >= m) continue;
			int na = a, nb = b, nc = c;
			movebit(na, nb);
			if(na != a || nb != b)
				que.push(tpl(na, nb, nc, count + 1));
			na = a, nb = b, nc = c;
			movebit(nb, nc);
			if(nb != b || nc != c)
				que.push(tpl(na, nb, nc, count + 1));
		}

		cout << ans  << endl;
	}
