  if( !a & !b ) return 0;
  if( c & 1 ) return( rec(a >> 1, b >> 1, c >> 1) );
  if( b & 1 ) return( rec(c >> 1, b >> 1, a >> 1) + rec((a|b|c) >> 1, 0, 0) + 1 );
