int main(){
  int N, M;
  while(cin >> N >> M && N){
    const int goal = (1 << N) - 1;
    int start[3] = {};
    int s_top[3] = {};
    memset(s_top, -1, sizeof(s_top));
    REP(i, 3){
      int k; cin >> k;
      REP(j, k) {
        int t; cin >> t;
        start[i] |= 1 << (t - 1);
        s_top[i] = max(s_top[i], t - 1);
      }
    }
    int ans = INF;
    for(int iter = 0; iter < 2; iter++){
      int last_move = iter; // 0 : A<->B, 1 : B<->C
      int state[3];
      int top[3];
      memcpy(state, start, sizeof(state));
      memcpy(top, s_top, sizeof(top));
      for(int time = 0; time <= M; time++){
        if(state[0] == goal || state[2] == goal){
          ans = min(ans, time);
          break;
        }
        int a, b;
        if(last_move == 0){
          last_move = 1;
          a = 1, b = 2;
        }else{
          last_move = 0;
          a = 0, b = 1;
        }
        if(top[a] > top[b]) swap(a, b);
        assert(top[b] >= 0); // i can move it
        top[a] = top[b]; 
        state[a] |= 1 << top[a]; // add top[a]
        state[b] &= ~(1 << top[a]); // remove top[a]
        // find next top
        for(int i = top[a] - 1; i >= -1; i--){
          if(i == -1 || (state[b] >> i & 1)){
            top[b] = i;
            break;
          }
        }
      }
    }
