using namespace std;


int n, m;
int ans;

inline int msb(int x) {
	if(!x) return 0;
	return (1 << (31 - __builtin_clz(x)));
}


void dfs(vector<int> state, vector<int> prev, int cnt = 0) {
	if(cnt > min(m, ans))
		return;

	if(state[0] == (1 << n) - 1 || state[2] == (1 << n) - 1) {
		ans = cnt;
		return;
	}

	for(int i = 0; i < 2; ++i) {
		int move = max(msb(state[i]), msb(state[i + 1]));
		vector<int> next = state;
		next[i] ^= move;
		next[i + 1] ^= move;
		if(next != state)
			dfs(next, state, cnt + 1);
	}	

}

int main() {
	cin.tie(false);
	ios::sync_with_stdio(false);

	const vector<int> init(3, -1);

	for(; cin >> n >> m, n | m;) {
		vector<int> cup(3, 0);

		for(int i = 0; i < 3; ++i) {
			int num;
			cin >> num;

			for(int j = 0; j < num; ++j) {
				int size;
				cin >> size;
				--size;

				cup[i] |= (1 << size);
			}
		}

		ans = INT_MAX;
		dfs(cup, init);

		cout << (ans == INT_MAX ? -1 : ans) << endl;
	}

	return EXIT_SUCCESS;
}

