      if(s.t[j] & 1 << i) tmp[j] = max(tmp[j], i);
    }
  }
  for(int i=0;i<N;i++){
    if(tmp[i] != -1){
      for(int j=0;j<N;j++){
        if(abs(i-j) != 1) continue;
        if(i == pret && j == pres) continue;
        if(tmp[i] > tmp[j]){
          S t = s;
          t.t[i] &= ~(1 << tmp[i]);
          t.t[j] |= 1 << tmp[i];
          t.cost++;
          res = min(res, calc(t, i, j));
          //ans = min(ans, res);
        }
      }
    }
  }
  return res;
}

int solve(S s){
  if(s.t[0] == (1 << n) - 1 || s.t[2] == (1 << n) - 1) return s.cost;
  int res = INF;
  int tmp[N];
  fill(tmp, tmp+N, -1);
  for(int i=n-1;i>=0;i--){
    for(int j=0;j<N;j++){
      if(s.t[j] & 1 << i) tmp[j] = max(tmp[j], i);
    }
  }
  for(int i=0;i<N;i++){
    if(tmp[i] != -1){
      for(int j=0;j<N;j++){
        if(abs(i-j) != 1) continue;
        if(tmp[i] > tmp[j]){
          S t = s;
          t.t[i] &= ~(1 << tmp[i]);
          t.t[j] |= 1 << tmp[i];
          t.cost++;
          res = min(res, calc(t, i, j));
          //ans = min(ans, res);
        }
      }
    }
  }
  if(res == INF) return -1;
  return res;
}

