using namespace std;

int n,m;
deque<int> deq[3];

int simulate(int sp,int dir) {
  if( sp+dir < 0 || sp+dir >= 3 || deq[sp].empty() ) return INT_MAX;
  if( (int)deq[0].size() == n ) return 0;


  deque<int> cup[3];
  rep(i,3) rep(j,(int)deq[i].size()) cup[i].push_back(deq[i][j]);
  int cur = sp;
  int next = cur + dir;
  cup[next].push_back(cup[cur].back()); cup[cur].pop_back();
  int cnt = 1;
  while( (int)cup[0].size() < n ) {
    /*
    cout << cnt << "::" << endl;
    rep(i,3) {
      cout << "$";
      rep(j,(int)cup[i].size()) cout<< cup[i][j] << " "; cout << endl;
    }
    puts("--");
    cout << "(" << cur << "," << next << ")" << endl;
    */
    int tmp = -1, prev = -1;
    rep(i,3) {
      if( i == cur || i == next ) continue;
      tmp = i;
    }
    prev = cur; cur = next; 
    //cout << tmp << "," << prev << endl;
    if( abs(tmp-prev) >= 2 ) {
      tmp = cur;
    }

    if( cup[prev].empty() && cup[tmp].empty() ) {
      if( (int)cup[0].size() == n ) return cnt;
      return INT_MAX;
    } else if( cup[prev].empty() ) {
      next = prev;
      cup[next].push_back(cup[tmp].back()); cup[tmp].pop_back();
      ++cnt;
    } else if( cup[tmp].empty() ) {
      next = tmp;
      cup[next].push_back(cup[prev].back()); cup[prev].pop_back();
      ++cnt;
    } else if( cup[tmp].back() < cup[prev].back() ) {
      next = tmp;
      cup[next].push_back(cup[prev].back()); cup[prev].pop_back();
      ++cnt;
    } else {
      next = prev;
      cup[next].push_back(cup[tmp].back()); cup[tmp].pop_back();
      ++cnt;
    }
    /*
    cout << cnt << "::" << endl;
    rep(i,3) {
      cout << "$";
      rep(j,(int)cup[i].size()) cout<< cup[i][j] << " "; cout << endl;
    }
    cout << "(" << cnt << ")--" << endl;
    */
    if( cnt > m ) return INT_MAX;
  }
  return cnt;
}

void compute() {
  int ans = INT_MAX;
  rep(sp,3) {
    for(int dir=-1;dir<=1;dir+=2) {
      ans = min(ans,simulate(sp,dir));
    }
  }
  if(ans == INT_MAX) cout << -1 <<endl;
  else cout << ans << endl;
}

int main() {
  while(cin >> n >> m,n|m) {
