int dp[14400000];
int N, M;
int to_hash(const vector<int>&nums) {
	int hash = 0;
	for (auto n : nums) {
		hash = 3 * hash + n;
	}
	return hash;
}
int getans(vector<int>&starts) {
	queue<pair<vector<int>, int>>que;
	que.emplace(starts, 0);
	while (!que.empty()) {
		auto v = que.front();
		que.pop();
		vector<int>nums = v.first;
		int now = v.second;
		const int hash = to_hash(nums);
		if (dp[hash] == false) {
			continue;
		}else {
			dp[hash] = false;
			if (now >= M+1) {
				return M + 1;
			}
			if (to_hash(nums) == 0) {
				return now;
			}
			else {
				dp[hash] = false;
				vector<int>tops(3, -1);
				for (int i = 0; i < N; ++i) {
					tops[nums[i]] = i;
				}
				int ans = M;
				if (tops[0] < tops[1]) {
					if (tops[1] != -1) {
						nums[tops[1]] = 0;
						que.emplace(nums, now + 1);
						nums[tops[1]] = 1;
					}
				}
				else {
					if (tops[0] != -1) {
						nums[tops[0]] = 1;
						que.emplace(nums, now + 1);
						nums[tops[0]] = 0;
					}
				}
				if (tops[1] < tops[2]) {
					if (tops[2] != -1) {
						nums[tops[2]] = 1;
						que.emplace(nums, now + 1);
						nums[tops[2]] = 2;
					}
				}
				else {
					if (tops[1] != -1) {
						nums[tops[1]] = 2;
						que.emplace(nums, now + 1);
						nums[tops[1]] = 1;
					}
				}
			}
		}
	}
	return M+1;

}

int main() {
	while (1) {
		for (int i = 0; i < 14400000; ++i) {
			dp[i] = true;
		} cin >> N >> M;
		if (!N)break;
		vector<int>nows(N);
		for (int i = 0; i < 3; ++i) {
			int a; cin >> a;
			for (int j = 0; j < a; ++j) {
				int b; cin >> b; b--;
				nows[b] = i;
			}
		}
		int ans = getans(nows);
		if (ans == M+1)ans = -1;
		cout << ans << endl;
	}

	return 0;
}

