#include <iostream>
#include <bitset>
#include <queue>
using namespace std;

int radix[17];
int end[16][2];

void mk_radix() {
	int x = 1;
	for(int i = 0; i < 17; i++) {
		radix[i] = x;
		x *= 3;
	}

	end[0][0] = 0;
	end[0][1] = 2;
	for(int i = 1; i < 16; i++) {
		end[i][0] = end[i-1][0] + radix[i]*0;
		end[i][1] = end[i-1][1] + radix[i]*2;
	}
}

int main() {
	int n, m;
	mk_radix();
	while(cin >> n >> m, n || m) {
		bitset<50000000> used;
		int in = 0;
		for(int i = 0; i < 3; i++) {
			int k;
			cin >> k;
			for(int j = 0; j < k; j++) {
				int a;
				cin >> a;
				in += i * radix[a-1];
			}
		}

		used.set(in);
		queue<int> q;
		q.push(in);
		int ans = 0;
		bool flg = false;
		while(!q.empty()) {
			queue<int> nex;
			while(!q.empty()) {
				int a = q.front();
				q.pop();
				int c[3] = {-1,-1,-1};
				int d = 0;

				if(a == end[n-1][0] || a == end[n-1][1]) {
					flg = true;
					goto EXIT;
				}

				int tmp = a;
				while(d < n) {
					c[tmp%3] = d;
					tmp /= 3;
					d++;
				}

				if(c[1] != -1 && c[1] > c[0]) {
					int e;
					e = (a / radix[c[1] + 1])*radix[c[1] + 1] + 0 * radix[c[1]] + a % radix[c[1]];
					if(!used[e]) {
						used.set(e);
						nex.push(e);
					}
				}
				if(c[1] != -1 && c[1] > c[2]) {
					int e;
					e = (a / radix[c[1] + 1])*radix[c[1] + 1] + 2 * radix[c[1]] + a % radix[c[1]];
					if(!used[e]) {
						used.set(e);
						nex.push(e);
					}
				}
				if(c[0] != -1 && c[0] > c[1]) {
					int e;
					e = (a / radix[c[0] + 1])*radix[c[0] + 1] + 1 * radix[c[0]] + a % radix[c[0]];
					if(!used[e]) {
						used.set(e);
						nex.push(e);
					}
				}
				if(c[2] != -1 && c[2] > c[1]) {
					int e;
					e = (a / radix[c[2] + 1])*radix[c[2] + 1] + 1 * radix[c[2]] + a % radix[c[2]];
					if(!used[e]) {
						used.set(e);
						nex.push(e);
					}
				}
			}
			ans++;
			q = nex;
		}
		EXIT:;
		if(ans <= m && flg)
			cout << ans << endl;
		else
			cout << -1 << endl;
	}
}

