using namespace std;

typedef struct Cup
{
	vector<int>P[3];
	int T;
	bool operator <(const Cup &a)const{ return T>a.T;};
}S;
int N,M;

int main()
{
	for(;scanf("%d%d",&N,&M),N;)
	{
		Cup tmp;
		int i,j,t,q,R=-1;
		for(i=0;i<3;i++)
		{
			for(scanf("%d",&t),j=0;j<t;j++)
			{
				scanf("%d",&q);
				tmp.P[i].push_back(q);
			}
		}

		tmp.T=0;
		priority_queue<Cup> Q;
		Q.push(tmp);
		while(!Q.empty())
		{
			tmp=Q.top();Q.pop();
			if(tmp.T>M)continue;
			if(tmp.P[0].size()==N||tmp.P[2].size()==N)
			{
				R=tmp.T;
				break;
			}
			for(int i=0;i<3;i++)
				for(int j=-1;j<2;j++)
				{
					if(i+j>=0&&i+j<3&&!tmp.P[i].empty())
					{
						int tmp1=tmp.P[i].back();
						int tmp2=-1;
						if(!tmp.P[i+j].empty())
							tmp2=tmp.P[i+j].back();
						if(tmp1>tmp2)
						{
							Cup ins=tmp;
							ins.P[i+j].push_back(tmp1);
							ins.P[i].pop_back();
							ins.T++;
							Q.push(ins);
						}
					}
				}
		}
		printf("%d\n",R);
	}
}

